export default function Hero() {
  return (
    <section className="relative w-full h-[535px] lg:h-[830px] overflow-hidden">
      {/* Background Image - 801px */}
      <div className="absolute inset-0 h-[801px]">
        <img
          src="https://api.builder.io/api/v1/image/assets/TEMP/3f88c4a1d6e38df6c0666710b2b102bc444ce77e?width=2880"
          alt="Airport background"
          className="w-full h-full object-cover"
        />
      </div>

      {/* Purple Overlay with Content - rgba(96, 73, 140, 0.60) */}
      <div
        className="absolute inset-0 h-[801px] flex items-start"
        style={{ background: "rgba(96, 73, 140, 0.60)" }}
      >
        <div className="container mx-auto px-4 lg:px-6 w-full">
          <div className="max-w-[922px] flex flex-col gap-8 lg:gap-12 pt-8 lg:pt-[220px]">
            {/* Title - 40px font, 850 weight, 18px line-height on desktop */}
            <h1
              className="text-white text-2xl sm:text-3xl lg:text-[40px] font-extrabold leading-tight lg:leading-[18px] tracking-tight uppercase"
              style={{ fontWeight: 850 }}
            >
              Connecting the World
            </h1>

            {/* Description - 24px font, 32px line-height on desktop */}
            <p className="text-white text-base sm:text-lg lg:text-2xl font-medium leading-relaxed lg:leading-8 tracking-tight max-w-[922px]">
              With Dammam Airports experience seamless journeys through our
              airports, serving over 60+ destinations where every arrival opens
              new possibilities.
            </p>
          </div>
        </div>
      </div>

      {/* Stats Section at Bottom - 130px height */}
      <div className="absolute bottom-0 left-0 right-0 h-[140px] bg-brand-purple overflow-hidden">
        <div className="container mx-auto px-4 lg:px-6 h-full flex items-center justify-center">
          <div className="inline-flex flex-row items-center justify-center gap-2 sm:gap-4 md:gap-8 lg:gap-[100px] overflow-x-auto overflow-y-hidden">
              <StatCard
                icon={<PassengerIcon />}
                numbers={["2"]}
                suffix="M+"
                label={
                  <>
                    PASSENGERS
                    <br />
                    PER YEAR
                  </>
                }
              />
              <StatCard
                icon={<DestinationIcon />}
                numbers={["10"]}
                suffix="+"
                label="DESTINATIONS"
              />
              <StatCard
                icon={<DevelopmentIcon />}
                numbers={["20"]}
                suffix="+"
                label={
                  <>
                    DEVELOPMENT
                    <br />
                    PROJECTS
                  </>
                }
              />
              <StatCard
                icon={<AirlineIcon />}
                numbers={["8"]}
                suffix="+"
                label="AIRLINES"
              />
          </div>
        </div>
      </div>
    </section>
  );
}

interface StatCardProps {
  icon: React.ReactNode;
  numbers: string[];
  suffix: string;
  label: React.ReactNode;
}

function StatCard({ icon, numbers, suffix, label }: StatCardProps) {
  return (
    <div className="relative w-[70px] sm:w-[80px] lg:w-[120px] h-[70px] sm:h-[80px] lg:h-[120px] flex flex-col items-center justify-start text-white flex-shrink-0">
      {/* Background */}
      <div className="absolute inset-0 bg-brand-purple"></div>
      
      {/* Icon */}
      <div className="relative mt-1 lg:mt-2">{icon}</div>

      {/* Number Display */}
      <div className="relative w-full h-[16px] lg:h-[21px] my-0.5 lg:my-1 flex items-center justify-center">
        <div className="inline-flex items-center justify-center gap-0.5 lg:gap-1">
          {/* Number */}
          <div className="text-base sm:text-lg lg:text-[24px] font-extrabold leading-tight lg:leading-[26.775px] tracking-[-0.319px]">
            {numbers[0]}
          </div>
          
          {/* Suffix */}
          <div className="text-base sm:text-lg lg:text-[24px] font-extrabold leading-tight lg:leading-[21px] tracking-[-0.25px]">
            {suffix}
          </div>
        </div>
      </div>

      {/* Label */}
      <div className="relative mt-auto mb-1 lg:mb-3 text-[8px] sm:text-[10px] lg:text-[12px] font-medium leading-tight lg:leading-[14px] tracking-[-0.25px] text-center px-1 lg:px-2">
        {label}
      </div>
    </div>
  );
}

